mapper.select:
select rown from(select rownum rown, seqno from 
(select b.* from bbs_info b where bbstype=#{bbstype} order by seqno asc))
where seqno=#{seqno}

select seqno from(select rownum rown, seqno, title from 
(select b.* from bbs_info b where bbstype=#{bbstype} order by seqno asc))
where rown=#{rn}
